import time
input = ""


def main():
    start = time.time()
    i = 0
    left = 0
    size = 14
    window = [0] * 26
    while (i < len(input) and i - left < size):
        c = ord(input[i])-97
        if (window[c] > 0 and window[c] > left):
            left = window[c] + 1
        window[c] = i
        i += 1
    print(time.time() - start)
    print(i)


main()
